import Vue from 'vue'
import Vuex from 'vuex'
import axios from "axios"

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    msg: 'Список групп',
    groupList: [
        {"optionName":"УИС-111",
          "optionValue":"1"},
      {
        "optionName":"УВА-411",
        "optionValue":"2"},
      {
        "optionName":"УИС-411",
        "optionValue":"3"},
      {
        "optionName":"УВА-111",
        "optionValue":"4"}
    ]

  },
  getters: {
    getMsg (state){
      return state.msg
    },
    getOptionList(state)
    {
      return state.groupList
    }
  },
  mutations: {
    setOptionList(state, optionList){
      state.groupList = optionList
    }
  },
  actions: {
    async loadOptionList(context){
      try{
        const url = '/kis/groups'
        const response = await axios.get(url)
        const results = response.data
        context.commit('setOptionList', results)
      } catch(err){
        if (err.response){
          console.log("Server Error:", err)
        } else if (err.request){
          console.log("Network Error", err)
        } else {
          console.log("Client Error",err)
        }
      }
    }
  },
  modules: {
  }
})
